    const [bufferLength, setBufferLength] = useState(null);
    const [dataArray, setDataArray] = useState(null);
    const [loaded, setLoaded] = useState(false);
    const [ultimate, setUltimatePeak] = useState(1);
    const fileInputRef = useRef(null);
    const frequencyRef = useRef(null);
    const backgroundRef = useRef(null);
    const scrubBarRef = useRef(null);
    const [play, setPlay] = useState(false);

    // Initialize audio context and elements when the file input changes
    function loadAudio() {
        if (audioElement) {
            audioElement.pause();
            audioElement.currentTime = 0;
        }
        setAudioCtx(new (window.AudioContext || window.webkitAudioContext)());
        setAudioElement(new Audio(), () => {
            audioElement.addEventListener("timeupdate", updateScrubBar);
            audioElement.src = URL.createObjectURL(fileInputRef.current.files[0]);
            audioElement.addEventListener("loadeddata", () => {
                setLoaded(true);
                scrubBarRef.value = 0;
            });
            audioElement.addEventListener("ended", () => {
                setPlay(false);
            });
            setSource(audioCtx.createMediaElementSource(audioElement), () => {
                setAnalyser(audioCtx.createAnalyser(), () => {
                    source.connect(analyser);
                    analyser.connect(audioCtx.destination);
                    setBufferLength(analyser.frequencyBinCount);
                    setDataArray(new Uint8Array(bufferLength));
                    setUltimatePeak(1);
                    drawVisualization();
                });
            });
        });
    }

    function updateScrubBar(){
        if(audioElement) {
            let currentTime = audioElement.currentTime;
            let duration = audioElement.duration;
            let scrubBarValue = (currentTime/duration)*100;
            scrubBarRef.value = scrubBarValue;
        }
    }

    function updatePlayback() {
        if(audioElement) {
            let scrubBarValue = scrubBarRef.value;
            let currentTime = (scrubBarValue / 100) * audioElement.duration;
            audioElement.currentTime = currentTime;
        }
    }

    useEffect(() => {
        //update sizes
        backgroundRef.current.width = window.innerWidth;
        backgroundRef.current.height = window.innerHeight;
        frequencyRef.current.width = window.innerWidth;
        frequencyRef.current.height = window.innerHeight;

        window.addEventListener("resize", () => {
            backgroundRef.current.width = window.innerWidth;
            backgroundRef.current.height = window.innerHeight;
            frequencyRef.current.width = window.innerWidth;
            frequencyRef.current.height = window.innerHeight;
        });
    }, []);

    function drawVisualization() {
        requestAnimationFrame(drawVisualization);
        analyser.getByteFrequencyData(dataArray);

        frequencyRef.current.fillStyle = "rgba(0, 0, 0, 0.1)";
        frequencyRef.current.fillRect(0, 0, frequencyRef.current.width, frequencyRef.current.height);

        let barWidth = (frequencyRef.current.width / bufferLength) * 2.5;
        let barHeight;
        let x = 0;

        for (let i = 0; i < bufferLength; i++) {
            barHeight = dataArray[i];
            let r = barHeight + (25 * (i/bufferLength));
            let g = 250 * (i/bufferLength);
            let b = 50;
            frequencyRef.current.fillStyle = "rgb(" + r + "," + g + "," + b + ")";
            frequencyRef.current.fillRect(x, frequencyRef.current.height - barHeight / 2, barWidth, barHeight / 2);
            x += barWidth + 1;
        }
    }

    function stopAudio() {
        if (audioElement) {
        audioElement.pause();
        audioElement.currentTime = 0;
        setPlay(false);
        }
    }

    function togglePlay() {
        if (audioElement) {
        if (audioElement.paused) {
            audioElement.play();
            setPlay(true);
        } else {
            audioElement.pause();
            setPlay(false);
        }
        }
    }*/

    return (
        <div style={props.style}>
            <br />
            <br />
            <input type="file" ref={fileInputRef} onChange={loadAudio} />
            {loaded?
            <div className="control-panel">
                <button onClick={togglePlay}>{play ? 'Pause' : 'Play'}</button>
                <button onClick={stopAudio}>Stop</button>
                <input ref={scrubBarRef} type="range" min="0" max="100" value={loaded ? 0 : 100} id="scrub-bar" disabled={!loaded} />
            </div>
            :
            <></>
            }
            <br />
            <canvas ref={frequencyRef} id="frequency" />
            <canvas ref={backgroundRef} id="background" />
        </div>
    );
}

export default AudioVisualizer;
