                requestAnimationFrame(drawVisualization);
                analyser.getByteFrequencyData(dataArray);

                // draw current frame to main canvas
                frequencyCtx.clearRect(0, 0, frequencyCtx.canvas.width, frequencyCtx.canvasheight);

